CREATE DEFINER=`root`@`localhost` PROCEDURE `add_to_cart`(IN userId INT, IN productId INT, IN size VARCHAR(20), IN quantity INT)
BEGIN
    DECLARE match_count INT;
    
    SELECT COUNT(*) INTO match_count 
    FROM vsneaker.shopping_cart_item sci 
    WHERE sci.User_ID = userId AND sci.Product_ID = productId AND sci.Size = size;
    
    IF (match_count > 0) THEN
        UPDATE vsneaker.shopping_cart_item sci 
        SET sci.Quantity = sci.Quantity + quantity 
        WHERE sci.User_ID = userId AND sci.Product_ID = productId AND sci.Size = size;
    ELSE
        INSERT INTO vsneaker.shopping_cart_item (User_ID, Product_ID, Size, Quantity) 
        VALUES (userId, productId, size, quantity);
    END IF;
END ;;
