CREATE DEFINER=`root`@`localhost` PROCEDURE `product_detail`(IN id INT)
BEGIN
	SELECT p.Product_name, p.Product_IMG, p.Price, p.Sku , p.Description , ca.Category_name, ca.Brand, d.Discount_name, d.Discount_percent
	FROM product p
	JOIN category ca ON ca.ID = p.Category_ID
	JOIN discount d ON d.ID = p.Discount_ID
	JOIN variation_size vs ON vs.Product_ID = p.ID
	WHERE p.ID = id
	GROUP BY p.ID;
    
    SELECT vs.Size, vs.Quantity
	FROM vsneaker.variation_size vs
	WHERE vs.Product_ID = id;
END ;;
